function get_area(e){$.ajax({url:"/async/area_non_tree",cache:!1,dataType:"json",data:{area_id:e},type:"POST",success:function(e){bind_area(e)}})}function bind_area(e){var n=$("#province_select"),a=$("#city_select"),o=$("#zone_select");if(null!=e){n.empty(),a.empty(),o.empty();for(var t=e.all_id_string,i=t.split("_"),p=i[0],l=i[1],r=i[2],c=e.province,d=e.city,v=e.zone,s=c.length,u=d.length,_=v.length,y="",f="",h="",m=0;s>m;m++){var g=c[m].name,b=c[m].id;y+=b==p?"<option selected value='"+b+"'>"+g+"</option>":"<option value='"+b+"'>"+g+"</option>"}for(var m=0;u>m;m++){var g=d[m].name,b=d[m].id;f+=b==l?"<option selected value='"+b+"'>"+g+"</option>":"<option value='"+b+"'>"+g+"</option>"}for(var m=0;_>m;m++){var g=v[m].name,b=v[m].id;h+=b==r?"<option selected value='"+b+"'>"+g+"</option>":"<option value='"+b+"'>"+g+"</option>"}n.append(y),a.append(f),o.append(h),""==h?o.hide():o.show()}}